# word func
def word_list_hangman():
    """
    the func take a word for the man chose one (randomly) and start the game

    :return: word
    """
    word_list = []
    print('5 words for the game')
    for i in range(5):
        word_input = input('enter a word for game list')
        word_list.append(word_input)
        random_word_for_the_list = word_list.choise(5)
        word_long = len(random_word_for_the_list)
        word = random_word_for_the_list
    global word
    global word_long


